import React from 'react'
import Contact from '../Components/Contacts';
import styled from 'styled-components';
import location from '../images/location.gif';
import message from '../images/message.gif';
import phone from '../images/phone.gif';

function ContactPage() {
    return (
        <ContactPageStyled>
        <div>
            <div className="ContactPage">
                <div className="map-sect">
                    <iframe src="https://www.google.pt/maps/place/Coimbra/@40.2288699,-8.4863144,12z/data=!3m1!4b1!4m5!3m4!1s0xd22f9144aacd16d:0x634564477b42a6b9!8m2!3d40.2033145!4d-8.4102573"
                    width="600" height="450" frameBorder="0" style={{border:0}} allowFullScreen="" aria-hidden="false" tabIndex="0"></iframe>
                </div>
                <div className="contact-sect">
                    <Contact icon={phone} text1={'+ 351 964501210 '}  title={'Phone'}/>
                    <Contact icon={message} text1={'tiago-delgado@sapo.pt'}  title={'Email'}/>
                    <Contact icon={location} text1={'Coimbra, '} text2={'Portugal'} title={'Location'}/>
                </div>
            </div>
        </div>
        </ContactPageStyled>
    )
}
const ContactPageStyled = styled.header`
.ContactPage{
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    margin-bottom: 6rem;
    @media screen and (max-width: 1270px){
        grid-template-columns: repeat(1, 1fr);
        .map-sect{
            width: 100%;
            height: 50vh;
            margin-bottom: 1rem;
        }
    }
    .contact-sect{
        width: 100%;
    }
}
.map-sect{
    width: 97%;
    padding: 1rem;
    background-color:#191D2B;
    iframe{
        width: 100%;
        height: 100%;
    }
}

.contact-sect{
    display: flex;
    justify-content: space-between;
    flex-direction: column;
}

.title{
    margin-bottom: 5rem;
}

`;
export default ContactPage;
