import React from 'react'
import Contacts from '../Components/Contacts';
import styled from 'styled-components';
import location from '../images/location.gif';
import message from '../images/message.gif';
import phone from '../images/phone.gif';

function ContactPage() {
    return (
        <ContactPageStyled>
        <div>
            <div className="ContactPage">
                <div className="map-sect">
                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d97475.50057551643!2d-8.486314356307988!3d40.22886990393581!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0xd22f9144aacd16d%3A0x634564477b42a6b9!2sCoimbra!5e0!3m2!1spt-PT!2spt!4v1629284834474!5m2!1spt-PT!2spt"
                width="600" height="450" frameBorder="0" style={{border:0}} 
                allowFullScreen="" aria-hidden="false" tabIndex="0"></iframe>
                </div>
                <div className="contact-sect">
                    <Contacts icon={phone} text1={'+ 351 964501210 '}  title={'Phone'}/>
                    <Contacts icon={message} text1={'tiago-delgado@sapo.pt'}  title={'Email'}/>
                    <Contacts icon={location} text1={'Coimbra, Portugal '} title={'Location'}/>
                </div>
            </div>
        </div>
        </ContactPageStyled>
    )
}
const ContactPageStyled = styled.header`
.ContactPage{
    padding: 3rem;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    margin-bottom: 6rem;
    @media screen and (max-width: 1270px){
        grid-template-columns: repeat(1, 1fr);
        .map-sect{
            width: 100%;
            height: 50vh;
            margin-bottom: 1rem;
        }
    }
    .contact-sect{
        width: 100%;
    }
}
.map-sect{
    width: 97%;
    padding: 1rem;
    background-color:#191D2B;
    iframe{
        width: 100%;
        height: 100%;
    }
}

.contact-sect{
    display: flex;
    justify-content: space-between;
    flex-direction: column;
}

.title{
    margin-bottom: 5rem;
}

`;
export default ContactPage;
