import React from 'react'
import MySkills from '../Components/MySkills'
import AboutMe from '../Components/AboutMe'
import logo from '../images/logo.png'
import styled from 'styled-components'

class Application extends React.Component {
    constructor(){
      super()
      this.scroll = this.scroll.bind(this)
    }
  scroll(direction){
      let far = $( '.image-container' ).width()/2*direction;
      let pos = $('.image-container').scrollLeft() + far;
      $('.image-container').animate( { scrollLeft: pos }, 1000)
    }
}

function AboutPage() {
    return (
        <div className="AboutPage">
            <AboutMe />
            <div className="skillsContainer">
                <MySkills skill={'Javascript'} progress={'60%'} width={'60%'}  />
                <MySkills skill={'React Js'} src={logo} progress={'50%'} width={'50%'} />
                <MySkills skill={'Java'} progress={'70%'} width={'70%'} />
                <MySkills skill={'Web Design'} progress={'75%'} width={'75%'} />
                <MySkills skill={'UI/Ux Design'} progress={'75%'} width={'75%'} />
            </div>
            <ImgStyled>
            <div className="main">
                <h1 className="title">Scrolling Image Slider</h1>
                <div className="wrapper">
                    <a className="prev" onClick={this.scroll.bind(null,-1)}>&#10094;</a>
                    <div className="image-container">
                    <div className="image">1</div>
                    <div className="image">2</div>
                    <div className="image">3</div>
                    <div className="image">4</div>
                    <div className="image">5</div>
                    <div className="image">6</div>
                    <div className="image">7</div>
                    <div className="image">8</div>
                    <div className="image">9</div>
                    <div className="image">10</div>
                    </div>
                    <a className="next" onClick={this.scroll.bind(null,1)}>&#10095;</a>
                </div>
            </div>;
            </ImgStyled>
    </div>
    )
}
const ImgStyled = styled.header`
width: 100%;
height: 100vh;
position: relative;

.typography{
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    width: 80%;

`;
export default AboutPage;
